import styles from "./burger-ingredients.module.css";
import { Tab } from '@ya.praktikum/react-developer-burger-ui-components';
import { Counter } from '@ya.praktikum/react-developer-burger-ui-components';
import { CurrencyIcon } from '@ya.praktikum/react-developer-burger-ui-components';
import BurgerIngredient from "../burger-ingredient/burger-ingredient";

function BurgerIngredients(props) {
  return (
    <section className={`${styles.section} pt-10`}>
      <h1 className="text text_type_main-large mb-5">Соберите бургер</h1>
      <nav className={styles.menu}>
        <Tab value="Булки">Булки</Tab>
        <Tab value="Соусы">Соусы</Tab>
        <Tab value="Начинки">Начинки</Tab>
      </nav>
      <ul className={`${styles.ingredients} custom-scroll mt-10`}>
        <li className={styles.type}>
          <BurgerIngredient group="Булки" ingredients={props} />
        </li>
        <li className={styles.type}>
          <h2 className="text text_type_main-medium mt-10 mb-6">Соусы</h2>
          <ul className={`${styles.block} pl-4`}>
            {props.ingredients.map((ingredient) => {
              return (
                ingredient.type === 'sauce' &&
                <li className={styles.ingredient}>
                  <Counter
                    count={1}
                    size="default"
                  />
                  <img src={ingredient.image} className="ml-4 mr-4"></img>
                  <div className={`${styles.price} mt-1 mb-1`}>
                    <p className="text text_type_digits-default mr-2">{ingredient.price}</p>
                    <CurrencyIcon type="primary" />
                  </div>
                  <p className={`${styles.ingredientname} text text_type_main-default`}>{ingredient.name}</p>
                </li>
              );
            })}
          </ul>
        </li>
        <li className={styles.type}>
          <h2 className="text text_type_main-medium mt-10 mb-6">Начинки</h2>
          <ul className={`${styles.block} pl-4`}>
            {props.ingredients.map((ingredient) => {
              return (
                ingredient.type === 'main' &&
                <li className={styles.ingredient}>
                  <Counter
                    count={1}
                    size="default"
                  />
                  <img src={ingredient.image} className="ml-4 mr-4"></img>
                  <div className={`${styles.price} mt-1 mb-1`}>
                    <p className="text text_type_digits-default mr-2">{ingredient.price}</p>
                    <CurrencyIcon type="primary" />
                  </div>
                  <p className={`${styles.ingredientname} text text_type_main-default`}>{ingredient.name}</p>
                </li>
              );
            })}
          </ul>
        </li>
      </ul>   
    </section>
  );  
}

export default BurgerIngredients;
